import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import $ from 'jquery';
import me from './img/me.png';
import weatherApp from './img/weatherApp.png';
import checkers from './img/checkers.png';

//global variables for application-wide use
let aboutStartPos;
let knowledgeStartPos;
let portfolioStartPos;
let contactStartPos;
let windowPosition;
let currentSection;
let homeEndPos;
let navHeight;
let nav;

class Title extends React.Component{
	render(){
		let title = this.props.id;
		title += "Title";
		let color = {color: this.props.color};
		return(
			<div id={title}><span style={color}>&lt;</span>{this.props.title}<span style={color}>/&gt;</span></div>
		);
	}
}

class TitleBox extends React.Component{
	render(){
		let titleBox = this.props.id;
		titleBox += "TitleBox";

		return(
			<div id={titleBox}>
				<Title id={this.props.id} title={this.props.title.toUpperCase()} color={this.props.color}/>
			</div>
		);
	}
}
class TitleContainer extends React.Component{

	render(){
		let title = this.props.id;
		title += "TitleContainer";

		return(
			<div id={title}>
				<TitleBox id={this.props.id} title={this.props.title} color={this.props.color}/>
			</div>
		);
	}
}


class Content extends React.Component{
	render(){
		let contentContainer = this.props.id + "ContentContainer";

		let content;
		switch (this.props.id){
			case "about":
				content = (
					<div id={contentContainer}>
						<div id="photoContainer">
		                	<img id="me" src={me} alt="me"/>
		            	</div>
		            	<div id="aboutTxtContainer">
		                	<p id="aboutMeTxt">
			                    Hello World! My name is Albert and I'm a 23 year old web developer based in the Bay Area! I'm a recent college graduate from the University of California, Davis with a Bachelor's of Science in Computer Science.<br/><br/>
			                    Regardless if its a static web page or a dynamic web application, I enjoy taking an idea and making it a reality! Being able to create something that's visually and functionally 
			                    beautiful is what motivates and excites me most about web development. It is my passion more so than my profession. As soon as I begin working on something, I become addicted and work almost non-stop. Each accomplishment 
			                    or milestone gives me great satisfaction. Each roadblock or obstacle gets more more motivated to overcome them! And when all is said and done, I have created something I am fully proud of.<br/><br/>

			                    My interests include Software Development and UI / UX Design. I am first and foremost a Software Developer, becoming a better and more creative designer over time. I enjoy playing 
			                    video games, watching basketball and movies, as well as just hanging out and socializing!<br/><br/>

			                    I'm always looking to better myself. If you'd like to learn more about what I can offer, continue on!<br/><br/>
			                    <em id="quote">"Tell me and I forget, teach me and I may remember, involve me and I learn"<br/></em>
			                    <em id="author">- Benjamin Franklin</em>
		                	</p>
	            		</div>
	            	</div>
				);
				break;
			case "knowledge":
				content = (
					<div id={contentContainer}>
						<div id="opentext">
							<p>There are countless programming languages and technologies available in the tech industry. Each person is unique in respect to what tools they have under their belt. For me, I find it extremely
							satisfying to discover something new that I can add to my arsenal. Click any
							<span style={{color: "#f76c6c"}}> <i className="fas fa-cog"></i></span> below to learn more about my skillset!</p>
						</div>

			            <div className="drop" id="currentContainer" onClick={() => this.props.onClick("current")}>
			            	<a id="currentCog"><i className="fas fa-cog"></i></a>
			                <a className="text">Present Skills</a>
			            </div>

			            <div className="drop" id="inProgressContainer" onClick={() => this.props.onClick("inProgress")}>
			            	<a id="inProgressCog"><i className="fas fa-cog"></i></a>
			                <a className="text">Working On Now</a>
			            </div>

			            <div className="drop" id="nextContainer" onClick={() => this.props.onClick("next")}>
			            	<a id="nextCog"><i className="fas fa-cog"></i></a>
			                <a className="text">Future Plans</a>
			            </div>

			            <div id="dropdown">
				            <div id="current">
			                    <i className="devicon-html5-plain-wordmark colored"></i>
			                    <i className="devicon-css3-plain-wordmark colored"></i>
			                    <i className="devicon-javascript-plain colored"></i>
			                    <i className="devicon-postgresql-plain-wordmark colored"></i>
			                    <i className="devicon-python-plain-wordmark colored"></i>
